import React, { Component, useContext } from "react";
import Compose from "../components/Compose";
import { NavLink } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";

class Viandes extends Component {
  state = {
    mesViandes: [
      {
        id: uuidv4(),
        img: "./img/viandes/escalope.png",
        type: "Escalope de poulet"
      },
      {
        id: uuidv4(),
        img: "./img/viandes/merguez.png",
        type: "merguez"
      },
      {
        id: uuidv4(),
        img: "./img/viandes/nuggets.png",
        type: "Nuggets"
      }
    ]
  };
  render() {
    let { mesViandes } = this.state;
    return (
      <div className=" relative myFund container text-center flex-col rounded-xl  bg-white h-3/5 w-2/4 ">
        <Compose />

        <div>
          <ul className="flex">
            {mesViandes.map(viande => (
              <li key={viande.id}>
                <img src={viande.img} className="w-40 h-40" alt="ok" />
              </li>
            ))}
          </ul>
        </div>

        <NavLink to="/sauces">
          <span className=" p-9 text-7xl">&#10093;</span>
        </NavLink>
      </div>
    );
  }
}

export default Viandes;
